var static_dir=document.location.protocol+"//static.jediholo.net/";var timezone=-18000000;function slideToggle(a){jQuery("#"+a).slideToggle()}function initMainNav(){jQuery("#mainNav li").each(function(){jQuery(this).data("originalMarginRight",jQuery(this).css("marginRight"));jQuery(this).hover(function(){jQuery(this).clearQueue().animate({marginRight:"-3px"},300)},function(){jQuery(this).clearQueue().animate({marginRight:jQuery(this).data("originalMarginRight")},300)})})}function getJediDate(e){var a=2000;var c=153;var d=e.getFullYear();var g=e.getMonth()+1;var f=c+((d-a)*12)+g;var b=e.getDate();if(d==2025&&g>8){f-=(g-8);b+=(g-8)*100}else{if(d>2025){f-=4}}return f+"."+b.toString().padStart(2,"0")}function getJediTime(b){var a=b.getHours().toString().padStart(2,"0");var c=b.getMinutes().toString().padStart(2,"0");var d=b.getSeconds().toString().padStart(2,"0");return a+c+"/"+d}function updateClock(){var d=new Date();var a=new Date(d.getTime()+(d.getTimezoneOffset()*60000)+timezone);var e=getJediDate(a);var c=getJediTime(a);var b=e+" ABY - "+c+" GST";jQuery("#clock").text(b);if(a.getHours()<5||a.getHours()>=21){jQuery(document.body).addClass("night")}else{jQuery(document.body).removeClass("night")}if(a.getHours()>=5&&a.getHours()<7){jQuery(document.body).addClass("sunrise")}else{jQuery(document.body).removeClass("sunrise")}if(a.getHours()>=19&&a.getHours()<21){jQuery(document.body).addClass("twilight")}else{jQuery(document.body).removeClass("twilight")}setTimeout(updateClock,1000)}function initClock(){updateClock()}function initMenu(){var a=jQuery.merge(jQuery(".navItem"),jQuery(".navList > li"));a.each(function(){var b=jQuery(this);var c=b.find("ul");c.css("display","block");c.css("opacity",0);b.data("help",c);b.hover(function(){jQuery(this).data("help").clearQueue().animate({opacity:1},300);jQuery("#clock").clearQueue().animate({opacity:0},300)},function(){jQuery(this).data("help").stop(true).animate({opacity:0},300);jQuery("#clock").clearQueue().delay(500).animate({opacity:1},300)})})}function initRandomHeader(){var c=jQuery(".headerContainer").first();var b=c.attr("id")===undefined?"header-default":c.attr("id");var a=Math.floor((Math.random()*3)+1);c.addClass(b+"-"+a)}function initCollapsableGroups(){jQuery(".collapsableGroup").each(function(){var e=jQuery(this).find(".groupHeader");var c=jQuery(this).find(".groupContent");var d=jQuery('<img class="arrow" src="'+static_dir+'img/arrow_down.gif" />');var a=d.clone();var b=new Array(d,a);e.prepend(d);e.append(a);e.css("cursor","pointer");e.click(function(f){c.slideToggle(300,function(){var g=(jQuery(this).css("display")=="none")?static_dir+"img/arrow_down.gif":static_dir+"img/arrow_up.gif";jQuery(b).each(function(){this.attr("src",g)})})});c.hide()})}function initTabs(){jQuery(".tabContainer").each(function(){var a=jQuery(this);var d=a.find(".tabContent");var b=a.find(".tabList");var c=b.find("li a");var e=b.find('li a[href="'+window.location.hash+'"]');if(e.length==0){e=jQuery(c[0])}b.show();d.each(function(){jQuery(this).attr("id","tab_"+this.id)});c.click(function(h){h.preventDefault();var f=jQuery(this);var g=this.hash.replace("#","#tab_");d.filter(g).fadeIn();d.not(g).hide();c.removeClass("active");f.addClass("active")});e.trigger("click")})}function initNewWinLinks(){jQuery("#phpbb .postbody .content a").each(function(){jQuery(this).attr("target","_blank")})}function initAnnouncement(){var c=null;var a="warning";if(c){var b=jQuery('<div class="global-announcement global-announcement-'+a+'">'+c+"</div>");jQuery("#contentContainer #content #contentHeader").after(b)}}jQuery(document).ready(function(){initMainNav();initClock();initMenu();initRandomHeader();initCollapsableGroups();initTabs();initNewWinLinks();initAnnouncement()});